"""
.. currentmodule:: swbutils.model.DataSource

Model to connect and query data from an external data source.
"""

from abc import abstractmethod, ABC

class DataSource(ABC):
    """
    DataSource interface
    """

    @abstractmethod
    def initialize_source(self, **kwargs):
        pass
    
    @abstractmethod
    def close_source(self, **kwargs):
        pass
    
    @abstractmethod
    def query(self, query_command):
        pass

    @abstractmethod
    def error(self, query_command):
        pass
    
